CREATE TABLE chair (id SERIAL PRIMARY KEY, name varchar NOT NULL, deanery varchar NOT NULL, UNIQUE (name));
CREATE TABLE student_group (id SERIAL PRIMARY KEY, numb varchar NOT NULL, chair varchar NOT NULL REFERENCES chair(name), UNIQUE (numb));
CREATE TABLE student (id SERIAL PRIMARY KEY, full_name varchar NOT NULL, passport varchar(10) NOT NULL, group_numb varchar REFERENCES student_group(numb));
